import React, { Component } from 'react';
import Product from '../components/Product';
import SideBar from '../components/SideBar';
import ButtonCart from '../components/ButtonCart';
import * as api from '../services/api';

export default class Home extends Component {
  constructor() {
    super();
    this.state = {
      query: '',
      products: [],
      loading: false,
      category: 'all',
      categoryId: '',
    };
  }

  handleChange = (event) => {
    const { value, name } = event.target;
    this.setState({
      [name]: value,
    });
  }

  getProducts = async () => {
    const { query, categoryId } = this.state;
    const apiProducts = await api.getProductsFromCategoryAndQuery(categoryId, query);
    return apiProducts.results;
  }

  handleClick = () => {
    const { category } = this.state;
    this.getProducts().then((response) => {
      this.setState({
        products: response,
        loading: true,
      });
    });
    this.handleFilter(category);
  }

  handleFilter = async (name) => {
    const categories = await api.getCategories();
    const filter = categories.find((category) => category.name === name);
    console.log(filter);
    this.setState({
      categoryId: filter.id,
    });
  }

  render() {
    const { products, loading } = this.state;
    console.log(this.state);
    return (
      <main>
        <label data-testid="home-initial-message" htmlFor="search">
          Digite algum termo de pesquisa ou escolha uma categoria.
          <input
            id="search"
            data-testid="query-input"
            onChange={ this.handleChange }
            name="query"
          />
          <button type="button" data-testid="query-button" onClick={ this.handleClick }>
            Buscar
          </button>
        </label>
        <SideBar handleChange={ this.handleChange } />
        <ButtonCart />
        <section>
          { loading ? products.map((product, index) => (<Product
            key={ index }
            product={ product }
          />)) : <span>Nenhum produto foi encontrado</span> }
        </section>
      </main>
    );
  }
}
