import React, { Component } from 'react';
import { Redirect } from 'react-router';
import { Loading, MovieForm } from '../components';
import * as movieAPI from '../services/movieAPI';

class EditMovie extends Component {
  constructor(props) {
    super(props);
    this.state = {
      shouldRedirect: false,
      status: 'loading',
      movie: {},
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    this.handleMovie();
    this.handleFetch();
  }

  handleFetch = () => {
    const { movieForm } = this.state;
    movieAPI.updateMovie(movieForm)
      .then(this.setState({
        shouldRedirect: true,
      }));
  }

  handleMovie = () => {
    const { match } = this.props;
    const { id } = match.params;
    console.log(id);
    movieAPI.getMovie(id)
      .then((resolve) => this.handleStateMovie(resolve));
  }

  handleStateMovie = (param) => {
    this.setState({
      movie: param,
    });
  }

  handleSubmit(updatedMovie) { // coloca o conteudo do formulario dentro do estado, updateMovie é um obejeto com varias chaves
    this.setState({
      movieForm: updatedMovie,
    });
  }

  handleReturn = () => {
    const { status, shouldRedirect } = this.state;
    if (shouldRedirect) {
      <Redirect />
    }

    if (status === 'loading') {
      <Loading />
    }
  }

  render() {
    const { movie } = this.state;
    return (
      <div data-testid="edit-movie">
        <MovieForm movie={ movie } onSubmit={ this.handleSubmit } />
        {this.handleReturn}
      </div>
    );
  }
}

export default EditMovie;
