// implement AddMovie component here
import React from 'react';
import AddImage from './FormAddMovie/AddImage';
import AddSubtitle from './FormAddMovie/AddSubtitle';
import AddTitle from './FormAddMovie/AddTitle';

// import PropTypes from 'prop-types';

class AddMovie extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      subtitle: '',
      title: '',
      imagePath: '',
      storyline: '',
      rating: 0,
      genre: 'action',
    };
  }

  handleChange = (event) => {
    this.setState({
      // estou pegando o valor do evento
      subtitle: event.target.value,
      title: event.target.value,
      imagePath: event.target.value,
      storyline: event.target.value,
      rating: event.target.value,
      genre: event.target.value,
    });
  }

  handleReset = (event) => {
    event.preventDefault();
    this.setState({
      subtitle: '',
      title: '',
      imagePath: '',
      storyline: '',
      rating: 0,
      genre: 'action',
    });
  }

  render() {
    const { onClick } = this.props;
    const { subtitle, title, imagePath, storyline, rating, genre } = this.state;
    return (
      <form data-testid="add-movie-form">

        <AddTitle value={ title } callback={ this.handleChange } />
        <AddSubtitle value={ subtitle } callback={ this.handleChange } />
        <AddImage value={ imagePath } callback={ this.handleChange } />

        <label data-testid="storyline-input-label" htmlFor="input-storyline">
          Sinopse
          <textarea
            value={ storyline }
            data-testid="storyline-input"
            onChange={ this.handleChange }
            id="input-storyline"
          />
        </label>

        <label data-testid="rating-input-label" htmlFor="input-rating">
          Avaliação
          <input
            type="number"
            value={ rating }
            data-testid="rating-input"
            onChange={ this.handleChange }
            id="input-rating"
          />
        </label>

        <label data-testid="genre-input-label" htmlFor="input-genre">
          Gênero
          <select
            value={ genre }
            data-testid="genre-input"
            onChange={ this.handleChange }
            id="input-genre"
          >
            <option value="action" data-testid="genre-option" selected>Ação</option>
            <option value="comedy" data-testid="genre-option">Comédia</option>
            <option value="thriller" data-testid="genre-option">Suspense</option>
          </select>
        </label>

        <button type="submit" data-testid="send-button" onClick={ this.handleReset }>Adicionar filme</button>

      </form>
    );
  }
}

// AddMovie.propTypes = {

// }

export default AddMovie;
